import{u as s}from"./cookie.760c8d35.js";import{l as t,z as a,A as i,k as n,m as e}from"./entry.4737a8fd.js";const r={id:"kmt"},l={key:0,class:"content"},u=e("h2",null,"WAT IS DE KMT?",-1),d=e("p",null,"Een weekje op kamp en zeilen met je toekomstige jaargenoten. Lijkt jou dat leuk? Kom dan mee op onze kennismakingstijd!",-1),h=e("p",null,"Bij ons is de KMT een leuk, extra weekje vakantie, zonder verplichtingen en geen ontgroening. Dit is het moment om verder kennis te maken met De Bolk en jouw jaargang goed te leren kennen. Er zijn geen verplichtingen aan je jaargang, het zal wel een groep mensen zijn die dezelfde ervaringen voor het eerst met jou mee zullen maken.",-1),g=e("p",null,"De eerste keer samen een groot feest organiseren, de eerste keer doorzakken tot 5 uur op De Bolk, samen strijden om je BSA te halen: allemaal ervaringen die je met je jaargenoten door zal maken. Je zal ook De Bolk verder leren kennen, de oudere leden die je zullen helpen commissies te vormen, het bestuur dat er altijd is om jouw vragen te beantwoorden, de oude lullen waarmee je altijd over alles kan zeuren tot de late uurtjes van de nacht. Dus start jouw tijd bij De Bolk goed en ga mee op kennismakingstijd!",-1),m=[u,d,h,g],k={key:1,class:"content"},c=e("h2",null,"WHAT IS THE KMT?",-1),_=e("p",null,"The KMT or KennisMakingsTijd (a period of getting to know each other) is a week of camping and sailing with your future companions. Does that sound fun? Then come along to our introduction time!",-1),j=e("p",null,"With us, the KMT is a fun, extra week of vacation, without obligations. This is the time to get to know The Bolk and to get to know your year.",-1),p=e("p",null,"The first time together to organize a big party, the first time sagging until 5 o'clock at The Bolk, fighting together to get your BSA: all experiences that you will experience with your yearlings. You will also get to know The Bolk, the older members who will help you form committees, the board that is always there to answer your questions, the old guys that always nag about everything until the late hours of the night. So start your time at The Bolk and go along with getting acquainted!",-1),w=[c,_,j,p],B={__name:"kmt",setup(f){const o=s("language",{maxAge:864e4,default:()=>"dutch"});return(v,y)=>(n(),t("div",r,[a(o)=="dutch"?(n(),t("div",l,m)):i("",!0),a(o)=="english"?(n(),t("div",k,w)):i("",!0)]))}};export{B as default};
